<?php

namespace Tests\Feature\Facade;

use Tests\TestCase;

class ImageTest extends TestCase
{
    /**
     * Base64エンコード画像データをリサイズできることを確認します。
     */
    public function test_resize_base64()
    {
        // 準備
        $data = 'data:image/jpeg;base64,';
        $image = \Feeldee\MediaBox\Facades\Image::create($data);
        $this->assertEquals(120, $image->width());
        $this->assertEquals(120, $image->height());

        // 実行
        $resizedImage = \Feeldee\MediaBox\Facades\Image::resize($data, 100, 100);

        // 評価
        $image = \Feeldee\MediaBox\Facades\Image::create($resizedImage);
        $this->assertStringContainsString('data:image/jpeg;base64', $resizedImage);
        $this->assertEquals(100, $image->width());
        $this->assertEquals(100, $image->height());
    }

    /**
     * ファイルパス画像データをリサイズできることを確認します。
     */
    public function test_resize_file_path()
    {
        // 準備
        $data = __DIR__ . '/../test_files/test_image.jpg';
        $image = \Feeldee\MediaBox\Facades\Image::create($data);
        $this->assertEquals(120, $image->width());
        $this->assertEquals(120, $image->height());

        // 実行
        $resizedImage = \Feeldee\MediaBox\Facades\Image::resize($data, 100, 100);

        // 評価
        $image = \Feeldee\MediaBox\Facades\Image::create($resizedImage);
        $this->assertStringContainsString('data:image/jpeg;base64', $resizedImage);
        $this->assertEquals(100, $image->width());
        $this->assertEquals(100, $image->height());
    }
}
